def sumAndAverage(number1, number2, number3):
    print(f"The sum is: {number1 + number2 + number3}")
    print(f"The average is: {(number1 + number2 + number3) / 3}")


# getting the inputs for the sum and average algorithm
number1 = float(input('Enter the first number: '))
number2 = float(input('Enter the second number: '))
number3 = float(input('Enter the third number: '))

# giving the results of the sum and average algorithm
sumAndAverage(number1, number2, number3)
