def sumAndProduct(number1, number2):
    print(f"The sum is: {number1 + number2}")
    print(f"The product is: {number1 *  number2}")


# getting the inputs for the sum and product algorithm
number1 = float(input('Enter the first number: '))
number2 = float(input('Enter the second number: '))

# giving the results of the sum and product algorithm
sumAndProduct(number1, number2)
